sys_script_include.script:
function validateEmailAddress(emailStr){
	//use Javascript coercion to guarantee emailStr is a string
	emailStr = emailStr + '';

	//If emailMatch has allowed syntax if return true else return false
	emailMatch = emailStr.match(/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);
	return emailMatch;
}
